  	 truncate: function(num, bits, twos_complement){
  	     var mod = 1<<bits;
  	     num = ((num % mod)+mod)%mod;
  	     return twos_complement ? (num >= 1<<(bits - 1) ? num - (1<<bits) : num) : num;
  	 },
